mapper.update:
UPDATE data_quality_error
date = #{date}, start_time = #{startTime}, end_time = #{endTime}, station_id = #{stationId}, data_source = #{dataSource}, code = #{code}, error_type = #{errorType}, num = #{num}
WHERE id = #{id}

mapper.select:
SELECT ROUND(SUM( total_num )/100000000, 2 ) AS totalNum, ROUND(SUM( qualified_num )/100000000, 2 ) AS qualifiedNum, ROUND( SUM( qualified_num )/ SUM( total_num )* 100, 2 ) AS qualifiedRate, cc.type_id FROM jb_qualified jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc on cc.station_id=jb.station_id where 1=1 and jb.station_id !=''
and cc.type_id=#{type}
and jb.date_time >= #{startTime}
and jb.date_time <= #{endTime}
and jb.station_id=#{stationId}
GROUP BY cc.type_id

SELECT ROUND( SUM( qualified_num )/ SUM( total_num )* 100, 2 ) AS qualifiedRate, jb.station_id , cc.station_name FROM jb_qualified jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc on cc.station_id=jb.station_id where 1=1 and jb.station_id !=''
and jb.date_time >= #{startTime}
and jb.date_time <= #{endTime}
and cc.type_id=#{type}
GROUP BY jb.station_id ORDER BY qualifiedRate LIMIT 10

SELECT dd.sort_jb FROM ( SELECT concat(@r:=@r+1, "") sort_jb, d1.* FROM ( SELECT d.* FROM ( SELECT ROUND( SUM( qualified_num ) , 2 ) AS qualifiedNum, cc.type_id , jb.station_id FROM jb_qualified jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.station_id = jb.station_id WHERE 1 = 1 AND jb.station_id != ''
and jb.date_time >= #{startTime}
and jb.date_time <= #{endTime}
GROUP BY cc.type_id,jb.station_id ORDER BY qualifiedNum desc )d join cloudiip_access_equipment.v_stations_codes sc on d.station_id=sc.station_id ORDER BY qualifiedNum desc )d1, (SELECT @r:=0) a )dd WHERE 1=1
and dd.station_id=#{stationId}

SELECT dd.sort_jb FROM ( SELECT concat(@r:=@r+1, "") sort_jb, d1.* FROM ( SELECT d.* FROM ( SELECT ROUND( SUM( qualified_num ) , 2 ) AS qualifiedNum, cc.type_id , jb.station_id FROM jb_qualified jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.station_id = jb.station_id WHERE 1 = 1 AND jb.station_id != '' and cc.type_id=(SELECT type_id from cloudiip_access_equipment.v_stations_codes where station_id=#{stationId} )
and jb.date_time >= #{startTime}
and jb.date_time <= #{endTime}
GROUP BY cc.type_id,jb.station_id ORDER BY qualifiedNum desc )d join cloudiip_access_equipment.v_stations_codes sc on d.station_id=sc.station_id ORDER BY qualifiedNum desc )d1, (SELECT @r:=0) a )dd WHERE 1=1
and dd.station_id=#{stationId}

SELECT
cc.station_name as station_id,
date_time,
ROUND(SUM( CASE `data_source` WHEN '1' THEN qualified_num ELSE 0 END )/SUM( total_num )* 100 ,2 )AS 'hlpt', ROUND(SUM( CASE `data_source` WHEN '2' THEN qualified_num ELSE 0 END )/SUM( total_num )* 100 ,2 ) AS 'djxt', ROUND(SUM( CASE `data_source` WHEN '3' THEN qualified_num ELSE 0 END )/SUM( total_num )* 100 ,2 ) AS 'czsssj', ROUND(SUM( CASE `data_source` WHEN '4' THEN qualified_num ELSE 0 END )/SUM( total_num )* 100 ,2 ) AS 'glycsj', ROUND( SUM( qualified_num )/ SUM( total_num )* 100, 2 ) AS pass_rate FROM jb_qualified jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc on cc.station_id=jb.station_id where 1=1 and jb.station_id !=''
and cc.type_id=#{type}
and jb.station_id=#{stationId}
and jb.date_time >= #{startTime}
and jb.date_time <= #{endTime}
GROUP BY
jb.station_id
date_time

SELECT
err.*,
cc.station_name
FROM
jb_data_quality_error err
LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.danji_code  LIKE CONCAT('%',err.station_id ,'%')
where 1=1

    and   err.data_source=#{dataSource}


    and   err.error_type=#{errorType}


    and   cc.station_id=#{stationId}


    and   err.date >= #{startTime}


    and   err.date <= #{endTime}

SELECT
count(*) as cnt,
sum(num) as num
FROM
jb_data_quality_error err

    LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.danji_code  LIKE CONCAT('%',err.station_id ,'%')

where 1=1

    and   err.data_source=#{dataSource}


    and   err.error_type=#{errorType}


    and   cc.station_id=#{stationId}


    and   err.date >= #{startTime}


    and   err.date <= #{endTime}

SELECT ROUND( SUM( CASE `error_type` WHEN '缺数' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'miss_data', ROUND( SUM( CASE `error_type` WHEN '死数' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'dead_data', ROUND( SUM( CASE `error_type` WHEN '错数' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'error_data', data_source FROM ( SELECT sum(num) num, data_source, error_type
,type_id
FROM jb_data_quality_error err
LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.danji_code LIKE CONCAT('%',err.station_id ,'%')
where 1=1
and type_id=#{type}
and cc.station_id=#{stationId}
and date >= #{startTime}
and date <= #{endTime}
GROUP BY data_source,error_type
,type_id
)a GROUP BY data_source

SELECT ROUND( SUM( CASE `data_source` WHEN '1' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'hlpt', ROUND( SUM( CASE `data_source` WHEN '2' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'djxt', ROUND( SUM( CASE `data_source` WHEN '3' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'czsssj', ROUND( SUM( CASE `data_source` WHEN '4' THEN num ELSE 0 END )/ sum( num )* 100, 2 ) AS 'glycsj', error_type FROM ( SELECT sum(num) num, data_source, error_type
,type_id
FROM jb_data_quality_error err
LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.danji_code LIKE CONCAT('%',err.station_id ,'%')
where 1=1
and type_id=#{type}
and cc.station_id=#{stationId}
and date >= #{startTime}
and date <= #{endTime}
GROUP BY data_source,error_type
,type_id
)a GROUP BY error_type

SELECT station_id,station_name,danji_code as station_code ,station_coordinate as lit ,type_id as stationType

from
cloudiip_access_equipment.v_stations_codes
where  1=1

    and   type_id=#{type}


    and   aname=#{aname}

SELECT device_id from jb_data_quality_error  err
LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.danji_code  LIKE CONCAT('%',err.station_id ,'%')
where 1=1

    and   data_source=#{dataSource}


    and   error_type=#{errorType}


    and   cc.station_id=#{stationId}


    and   type_id=#{type}

GROUP BY device_id
ORDER BY  CAST(device_id AS DECIMAL)

SELECT map.tablename,err.* from jb_dataquality_table_map map
                                    left join jb_data_quality_error err on map.tableno=err.table_no and map.datasource=err.data_source
where err.id=#{id}
