mapper.select:
SELECT
jb.ghi_value
jb.temp_value
jb.spd_value
as value, round( sum( jb.cnt )/97/24/60,2) cnt
,cc.aname
FROM
jb_distributed_radiation_dose
jb_distributed_avg_temp
jb_distributed_wind_speed
jb LEFT JOIN cloudiip_access_equipment.v_stations_codes cc ON cc.station_id = jb.station_id WHERE 1=1
and DATE_FORMAT( `data_time`, "%Y" )= #{year}
GROUP BY
jb.ghi_value ORDER BY CAST(jb.ghi_value AS DECIMAL);
jb.temp_value ORDER BY CAST(jb.temp_value AS DECIMAL);
jb.spd_value ORDER BY CAST(jb.spd_value AS DECIMAL);
,cc.aname UNION SELECT jb.ghi_value AS value, sum( jb.cnt ) cnt, "冀北" as aname FROM jb_distributed_radiation_dose jb WHERE 1=1
and DATE_FORMAT( `data_time`, "%Y" )= #{year}
GROUP BY
jb.ghi_value ORDER BY CAST(jb.ghi_value AS DECIMAL);
jb.temp_value ORDER BY CAST(jb.temp_value AS DECIMAL);
jb.spd_value ORDER BY CAST(jb.spd_value AS DECIMAL);
